// Validate CloudEvents Format
const event = $json;

// Required CloudEvents fields
if (!event.specversion || event.specversion !== '1.0') {
  throw new Error('Invalid CloudEvents version. Expected 1.0, got: ' + event.specversion);
}

if (!event.type) {
  throw new Error('Missing required CloudEvents field: type');
}

if (!event.source) {
  throw new Error('Missing required CloudEvents field: source');
}

if (!event.id) {
  throw new Error('Missing required CloudEvents field: id');
}

if (!event.data) {
  throw new Error('Missing required CloudEvents field: data');
}

// Validate event type pattern
const typePattern = /^com\.arise\.[a-z]+\.[a-z]+\.[a-z]+\.v[0-9]+$/;
if (!typePattern.test(event.type)) {
  throw new Error('Invalid event type format. Expected: com.arise.<domain>.<entity>.<action>.v<version>');
}

// TODO: Add AsyncAPI schema validation here
// Load schema from asyncapi.yaml and validate event.data

return { json: event };

// Your module's business logic here
const inputData = $json.data;
const correlationId = $json.id;

// ============================================
// TODO: Implement your processing logic here
// ============================================
// 
// Example for a validator module:
// const isValid = validateSensorReading(inputData);
// const result = {
//   ...inputData,
//   validationStatus: isValid ? 'valid' : 'invalid',
//   validatedAt: new Date().toISOString()
// };
//
// Example for an enricher module:
// const context = await loadContext(inputData.sensorId);
// const result = {
//   ...inputData,
//   location: context.location,
//   deviceType: context.deviceType
// };

const result = {
  ...inputData,
  processed: true,
  processedAt: new Date().toISOString(),
  processedBy: '{{MODULE_ID}}'
};

return { 
  json: {
    processedData: result,
    correlationId: correlationId
  }
};

// Create CloudEvents Response
const createCloudEvent = (data, eventType, correlationId) => {
  const event = {
    specversion: '1.0',
    type: `com.arise.{{DOMAIN}}.{{OUTPUT_ACTION}}.v1`,
    source: '{{MODULE_ID}}',
    id: `${$execution.id}_${Date.now()}`,
    time: new Date().toISOString(),
    datacontenttype: 'application/json',
    data: data
  };

  // Add correlation ID if present (for tracing)
  if (correlationId) {
    event.correlationid = correlationId;
  }

  return event;
};

const response = createCloudEvent(
  $json.processedData,
  '{{OUTPUT_EVENT_TYPE}}',
  $json.correlationId
);

return { json: response };

// Error Handler
const error = $json.error || $execution.error || 'Unknown error';
const errorMessage = typeof error === 'object' ? error.message : error;

const errorResponse = {
  specversion: '1.0',
  type: 'com.arise.{{DOMAIN}}.error.v1',
  source: '{{MODULE_ID}}',
  id: `${$execution.id}_error_${Date.now()}`,
  time: new Date().toISOString(),
  datacontenttype: 'application/json',
  data: {
    error: true,
    message: errorMessage,
    module: '{{MODULE_ID}}',
    timestamp: new Date().toISOString()
  }
};

return { json: errorResponse };
